actions.value:
var codes = '';
for(var i = 0; i < data.verification.recoveryCodes.length; i++){
  codes += '<h4 style="margin-left:0px;text-align:center; color:red;"><strong>' + data.verification.recoveryCodes[i] + '</strong></h4>';
}

if (codes && codes !== value) {
  value = codes;
}

twoFactorAuthenticationSettingsForm.controller:
function updateUserData() {
  Formio.makeStaticRequest(Formio.projectUrl + '/current', 'GET', null, { ignoreCache: true })
    .then(function(user) {
      if (user && user.data) {
      Formio.setUser(user);
      var newData = Object.assign(instance.data, user.data);
      instance.data = newData;
      instance.data.userLoaded = true;
      instance.triggerChange();
    }
  });
}

if (instance && Formio) {
  updateUserData();
  instance.on('verified', updateUserData);
  instance.on('qrLoaded', function() {
    if (data.twoFaMethod === 'turn-off') {
      updateUserData();
    }
  });
}

if (instance && instance.options) {
  if (!instance.options.hooks) {
    instance.options.hooks = {};
  }

  if (!instance.options.hooks.onTurnOffTwoFa) {
    instance.options.hooks.onTurnOffTwoFa = function(cb) {
      var isConfirmed = confirm('Are you sure you want to turn off Two-Factor Authentication?');

      cb(isConfirmed);
    }
  }
}
